import { jsx } from 'react/jsx-runtime';
import Heading from '../Heading/Heading.js';
import { useMedia } from '../Media/Media.js';
import useId from '../../../utils/useId.js';
import { breakpoints } from '../../../constants/tokens/breakpoints.js';

function useModalHeading(props) {
    const { heading , size  } = props;
    const isMobile = useMedia({
        maxWidth: breakpoints.breakpointMedium
    });
    const headingId = useId('ModalHeading');
    return [
        headingId,
        jsx(Heading, Object.assign({
            id: headingId,
            size: size === 'fullscreen' || isMobile ? 'medium' : 'large'
        }, {
            children: heading
        }), void 0), 
    ];
}

export { useModalHeading as default };
